function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),i=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active"),i.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active"),i.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active"),i.classList.remove("locked"))}))}burgerMenu();const navbar=document.querySelector(".navbar");window.onscroll=()=>{window.scrollY>200?navbar.classList.add("sticky"):navbar.classList.remove("sticky")};var swiper=new Swiper(".mySwiper",{slidesPerView:3,spaceBetween:50,pagination:{el:".swiper-pagination",clickable:!0},breakpoints:{200:{slidesPerView:1,spaceBetween:15},320:{slidesPerView:1,spaceBetween:15},375:{slidesPerView:1,spaceBetween:15},590:{slidesPerView:2,spaceBetween:20},768:{slidesPerView:2,spaceBetween:40},868:{slidesPerView:3,spaceBetween:40},1024:{slidesPerView:3,spaceBetween:40},1440:{slidesPerView:4,spaceBetween:40},1920:{slidesPerView:3,spaceBetween:50}}});const footerFormBtn=document.getElementById("form-btn");footerFormBtn.addEventListener("click",(()=>{alert("Сайт знаходиться в розробці")}));